%limpiar variables y ventanaclearclc%crear una variable xsyms x;%Pedir ecuacionprintf('Analizar funcion del tipo f(x)=ax+b\n')a=input('ingrese a:');b=input('ingrese b:');f(x)=a*x+b%calculo de el intersecto en xif(a==0)fprintf('\nNo hay intercepto en x');elseif(b==0)fprintf('El intersepto en x 0.00');elsex1=-b/a;fprintf('El intersepto en x es %.2f', x1);endifendif%calculo de el intersecto en yy1=b;fprintf(', el intersepto en y es %.2f',y1);%pendientem=a;printf('\nLa pendiente es %d',m);%Dominio y rangoprintf('\nEl dominio de la funcion son los Reales');if (a==0)printf('\nEl rango es %2.f\n', b);elseprintf('\nEl rango de la funcion son los Reales\n');endifif(m>0)printf('\nLa grafica es creciente\n');endifif(m<0)printf('\nLa grafica es decreciente\n');endifif(m==0)printf('\nLa grafica es paralela al eje x\n');endif%graficargrid;x=(-3):1:(3);y=a*x+b;plot(x,y);fprintf('\n ');fprintf('\n------------------------------------------------------------------------------');clear;printf('\nEscriba la letra en minuscula de la funcion que quiera analizar: \n');printf('a. Funcion lineal de la forma f(x)=ax+b \n');printf('b. Funcion cuadratica de la forma f(x)=ax^2+b \n');printf('c. Funcion de valor absoluto de la forma f(x)=k|ax+b| \n');printf('d. Funcion radical de la forma f(x)=raiz(ax+b)+c \n');printf('e. Funcion logaritmica de la forma f(x)=log_basea(bx+c)\n');printf('f. Funcion exponencial de la forma f(x)=a^(bx+c)+d \n');printf('g. Funcion racional de la forma f(x)=p(x)/q(x), donde p(x)=ax+b y q(x)=cx+d \n');