%limpiar variables y ventanaclearclcsyms x;%Pedir ecuacionprintf('Analizar funcion del tipo f(x)=ax^2+bx+c\n');doa=input('ingrese a:');if(a==0)printf('a debe ser distinto de cero\n');endifuntil(a!=0)b=input('ingrese b:');c=input('ingrese c: ');f(x)= a*x^2 +b*x+c%verticex1=-b/(2*a) ;y1=a*(x1^2)+b*x1+c;fprintf('\nEl vertice esta en [%.2f', x1);fprintf(',%.2f]', y1);%eje de simetriafprintf('\nEl eje de simetria esta en x=%.2f', x1);%dominiofprintf('\nDominio: R');%rangoif(a>0)fprintf('\nRango: [%.2f,+inf)',x1);elsefprintf('\nRango: (-inf,%.2f',x1);endif%interceptosd=b^2-4*a*c;if(d>=0)ix1=(-b+sqrt(d))/2*a;ix2=(-b-sqrt(d))/2*a;fprintf('\nIntercepto en x=%.2f',ix1);fprintf(' y en x=%.2f',ix2);fprintf('\nIntercepto en y=%.2f',c);elsefprintf('\nNo hay intercepto en x');fprintf('\nIntercepto en y=%.2f',c);endif%crecimientoif(a>0)fprintf('\nCrece de [%.2f,+inf)',x1);elsefprintf('\ncrece de (-inf,%.2f]\n',x1);endif%graficamosgrid;x=(-3):0.2:(3);y = a*(x.^2)+b*x+c;plot(x,y);fprintf('\n ');fprintf('\n------------------------------------------------------------------------------');clear;printf('\nEscriba la letra en minuscula de la funcion que quiera analizar: \n');printf('a. Funcion lineal de la forma f(x)=ax+b \n');printf('b. Funcion cuadratica de la forma f(x)=ax^2+b \n');printf('c. Funcion de valor absoluto de la forma f(x)=k|ax+b| \n');printf('d. Funcion radical de la forma f(x)=raiz(ax+b)+c \n');printf('e. Funcion logaritmica de la forma f(x)=log_basea(bx+c)\n');printf('f. Funcion exponencial de la forma f(x)=a^(bx+c)+d \n');printf('g. Funcion racional de la forma f(x)=p(x)/q(x), donde p(x)=ax+b y q(x)=cx+d \n');