%limpiar variables y ventanaclearclc%crear una variable xsyms x;%Pedir ecuacionprintf('Analizar funcion del tipo f(x)=k|ax+b|+c, escriba "menu" si quiere volver al menu inicial\n')k=input('ingrese k:');a=input('ingrese a:');b=input('ingrese b:');c=input ('Ingrese c:');f(x)=k*abs(a*x+b)+c%calculo de  intersectosif(k<0 && c>0)ix1=(-b-c/k)/a;ix2=(-b+c/k)/a;fprintf('interceptos en x1: %.2f,',ix1);fprintf('y x2: %.2f',ix2);fprintf('\nintercepto en y: %.2f',k*abs(b)+c);endifif(k<0 && c<0)ix1=(-b-c/k)/a;ix2=(-b+c/k)/a;fprintf('\nNo hay intercepto en x');fprintf('\nintercepto en y: %.2f',k*abs(b)+c);endifif(k>0 && c>0)ix1=(-b-c/k)/a;ix2=(-b+c/k)/a;fprintf('\nNo hay intercepto en x');fprintf('\nintercepto en y: %.2f',k*abs(b)+c);endifif(k>0 && c<0)ix1=(-b-c/k)/a;ix2=(-b+c/k)/a;fprintf('interceptos en x1: %.2f,',ix1);fprintf('y x2: %.2f',ix2);fprintf('\nintercepto en y: %.2f',k*abs(b)+c);endif%Dominiofprintf('\nEl dominio son los reales');%rangoif(k>0)fprintf('\nEl rango es: [ %.2f,',c);fprintf('+inf [');elsefprintf('\nEl rango es: [ %.2f,',c);fprintf('-inf [');endif%verticefprintf('\nVertice: ( %.2f,',-b/a);fprintf(' %.2f )',c);%eje de simetriafprintf('\nEje de simetria x=%.2f', -b/a );%creciminetofprintf('\nCrecimiento:[%.2f',-b/a );fprintf(',+inf)');fprintf('\nDecrecimiento: (-infinito, %.2f]',-b/a );%graficargrid;x=(-3):0.01:(3);y=k*abs(a*x+b)+c;plot(x,y);fprintf('\n ');fprintf('\n------------------------------------------------------------------------------');clear;printf('\nEscriba la letra en minuscula de la funcion que quiera analizar: \n');printf('a. Funcion lineal de la forma f(x)=ax+b \n');printf('b. Funcion cuadratica de la forma f(x)=ax^2+b \n');printf('c. Funcion de valor absoluto de la forma f(x)=k|ax+b| \n');printf('d. Funcion radical de la forma f(x)=raiz(ax+b)+c \n');printf('e. Funcion logaritmica de la forma f(x)=log_basea(bx+c)\n');printf('f. Funcion exponencial de la forma f(x)=a^(bx+c)+d \n');printf('g. Funcion racional de la forma f(x)=p(x)/q(x), donde p(x)=ax+b y q(x)=cx+d \n');