%limpiar variables y ventanaclearclcsyms x;%variablesprintf("Funcion Racional F(x)=P(x)/q(x), donde p(x)=ax+b y q(x)=cx+d\n ")dodoa=input("valor de a: ");if(a==0)fprintf("Ingrese un valor de a distinto a cero\n");endifuntil(a!=0)b=input("valor de b: ");doc=input("valor de c: ");if(c==0)fprintf("Ingrese un valor de c distinto a cero\n");endifuntil(c!=0)d=input("valor de d: ");if(a+b==c+d)fprintf("Ingrese valores a!=c o b!=d para que p(x)/q(x) no sea igual a 1\n");endifuntil(a+b!=c+d);f(x)= (a*x+b)/(c*x+d)%dominiodominio=-(d)/c;fprintf('El dominio de la funcion es: |R- [%.2f',dominio);fprintf(']\n');%Rangorango=a/c;fprintf('El rango de la funcion es: |R- [%.2f',rango);fprintf(']\n');%asintota horizontalAH=a/c;printf('La asintota horizontal es: ');fprintf('(0 en X ,%.2f',AH);fprintf(' en Y)\n');%asintota verticalAV=-(d)/c;printf('La asintota Vertical es: (%.2f',AV);fprintf(',0)\n');% intercepto en xIterx=-(b)/a;printf('intercepto en x es: (%.2f',Iterx);fprintf(')\n');% intercepto en yItery=b/d;printf('intercepto en Y es: (%.2f',Itery);fprintf(')\n');%crecimiento derivada=(-b*c+a*d)/c^2;if (derivada>0)        fprintf('La funcion crece de (-inf,%.2f',-d/c');        fprintf('[ y de ]%.2f,+inf)',-d/c);else    fprintf('La funcion decrece de (-inf,%.2f',-d/c);    fprintf("[ y de ]%.2f,+inf)",-d/c);  endif%graficargrid;x = [-10:0.01:10];y = (a*x+b)./(c*x+d);x1=AV;plot(x,y);fprintf('\n ');fprintf('\n------------------------------------------------------------------------------');clear;printf('\nEscriba la letra en minuscula de la funcion que quiera analizar: \n');printf('a. Funcion lineal de la forma f(x)=ax+b \n');printf('b. Funcion cuadratica de la forma f(x)=ax^2+b \n');printf('c. Funcion de valor absoluto de la forma f(x)=k|ax+b| \n');printf('d. Funcion radical de la forma f(x)=raiz(ax+b)+c \n');printf('e. Funcion logaritmica de la forma f(x)=log_basea(bx+c)\n');printf('f. Funcion exponencial de la forma f(x)=a^(bx+c)+d \n');printf('g. Funcion racional de la forma f(x)=p(x)/q(x), donde p(x)=ax+b y q(x)=cx+d \n');